INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (10,'Mon 12 Jul 09:49:29 1999 NZST',10,'andrew','Add ''Cheque Sent'' functionality','This update includes a database change to handle a ''Date Cheque Posted'' as well as changes to the unpresented cheques browser to show the date and allow it to be set.
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (13,'Fri 23 Jul 08:46:13 1999 NZST',10,'andrew','Building Profile Report','Changed building profile so that the number of rows of tenant information cycles within a level, rather than beyond.
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (11,'Mon 12 Jul 09:52:31 1999 NZST',10,'andrew','Fix problem with debtors report','There was a problem with the debtors report not showing the extended aging information if a ''minimum value'' of less than 0.02c was used. This update will ensure the report works with a minimum value of 0.01c.','http://wrms.cat-it.co.nz/system_updates/update-11.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (16,'Tue 27 Jul 09:55:47 1999 NZST',10,'andrew','Show vacant space on Rent Variance report','Fixed report to show vacant space (difference between property and tenants) when run for tenants, detailed.','http://wrms.cat-it.co.nz/system_updates/update-16.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (14,'Fri 23 Jul 09:34:29 1999 NZST',10,'andrew','Building Profile Image - move title left 1cm','Move title left by 1cm for both landscape and portrait layouts','http://wrms.cat-it.co.nz/system_updates/update-14.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (18,'Thu 14 Oct 16:02:02 1999 NZDT',10,'andrew','Fix order of totals on Aged Balance report','This should put the totals back in the right order on the Aged Balance report.','http://wrms.cat-it.co.nz/system_updates/update-18.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (15,'Fri 23 Jul 10:22:04 1999 NZST',10,'andrew','Building Profile Image Printer - move title left by 2cm','Move title left by 2cm for both landscape and portrait layouts','http://wrms.cat-it.co.nz/system_updates/update-15.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (17,'Tue 27 Jul 13:13:38 1999 NZST',10,'andrew','Zip containing new APMS.XLA','This is <B>NOT</B> a normal update.  This zipfile contains APMS.XLA to replace the existing one in your Office97 "Add Ins" directory.','http://wrms.cat-it.co.nz/system_updates/update-17.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (19,'Fri 15 Oct 17:45:39 1999 NZDT',10,'andrew','Fix forecasting problem','Create actions for re-leasing of areas which fall vacant between today and the start of the forecasting period, ensuring that they get leased.','http://wrms.cat-it.co.nz/system_updates/update-19.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (12,'Tue 13 Jul 15:05:09 1999 NZST',24,'tyrone','Update to the unpresented cheques report','Removed the total line "Balance if sent cheques are presented"
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (20,'Mon 18 Oct 16:31:28 1999 NZDT',10,'andrew','Start vacant period from forecast start if prior','For leases ending between now and the forecast start, the vacant period should start from the forecast start date.','http://wrms.cat-it.co.nz/system_updates/update-20.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (1,'Thu 13 May 07:40:34 1999 NZST',10,'andrew','Update Management Reports','Update Management Reports to show Contracted Rents rather than Charged Rents','/~andrew/capital/system_updates/update-1.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (2,'Fri 14 May 11:37:00 1999 NZST',10,'andrew','Update to add debug logging to lease wizard','Update to add debug logging to lease wizard, some small fixes to the Property Verification report.
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (3,'Fri 14 May 11:46:58 1999 NZST',10,'andrew','Update to TenancyLease and RentalSpace triggers','Update to TenancyLease and RentalSpace triggers to try and avoid deadlock conditions during various wizards doing lease operations.','/~andrew/capital/system_updates/update-3.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (7,'Fri 02 Jul 12:59:35 1999 NZST',10,'andrew','Problems with outgoings on rent charge basis','Fixes to Rent Charge process for AGP.','http://wrms.cat-it.co.nz/system_updates/update-7.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (22,'Wed 27 Oct 07:43:41 1999 NZDT',10,'andrew','Hide Charged Rents and Notes on Property Schedule','I have added a ''No Charged Rents'' and a ''No Notes'' option onto the schedule.  When used together this should give Colin what he is asking for.','http://wrms.cat-it.co.nz/system_updates/update-22.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (26,'Wed 24 Nov 16:14:37 1999 NZDT',10,'andrew','Change to debtors report to correct order of aged totals at end of report','The debtors report was displaying the aged balances at the end of the report in the wrong order.  This update corrects that.','http://wrms.cat-it.co.nz/system_updates/update-26.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (24,'Wed 24 Nov 13:26:49 1999 NZDT',10,'andrew','Changes to Outgoings Export','The export has been changed to try and better identify cases where carpark-only tenants have been inappropriately set to ''Net Lease'' and given a recovery percentage of 100.
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (25,'Wed 24 Nov 13:48:22 1999 NZDT',10,'andrew','Changes to AKLD Schedule','The AKLD Schedule has been corrected to apply the same logic as I just changed the Outgoings export to.','http://wrms.cat-it.co.nz/system_updates/update-25.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (28,'Mon 29 Nov 15:17:14 1999 NZDT',10,'andrew','Balances Only option for Property Transactions report','This update adds a Balances Only option to the Property Transactions report.','http://wrms.cat-it.co.nz/system_updates/update-28.zip','APMS');
INSERT INTO system_update (update_id,update_on,update_by_id,update_by,update_brief,update_description,file_url,system_code) values (29,'Thu 02 Dec 10:22:28 1999 NZDT',10,'andrew','Property & GL Transactions non-zero at end of period','Changes to the Property and GL Transactions reports to have the ''non-zero'' option apply at the end of period reported, rather than for the duration of the period reported.
